import { observer } from 'mobx-react-lite';
import React, { useContext, useState } from 'react';
import { Context } from '../index';

const TypeBar = observer(() => {
    const [carTypes, setCarTypes] = useState(false)
    const {device} = useContext(Context)

    return (
        <div className='d-flex'>
            <div class="dropdown">
                <button onClick={()=>setCarTypes(!carTypes)} class="btn btn-secondary dropdown-toggle" type="button" >
                    Dropdown button
                </button>
                {carTypes ? <div className ="dropup-menu">
                {device.types.map(type => (<div key={type.id} onClick={()=> device.setSelectedType} className='d-flex align-items-center'>
                    <p className='m-0 ms-1 fs-7'>{type.name}</p>
                </div>)
            ) } </div>
                : 
                <ul className = "dropdown-menu"></ul>
                }
            </div>
        </div>
    );
});

export default TypeBar;
