import React, { useContext, useEffect } from 'react';
import TypeBar from '../components/TypeBar';
import BrandBar from '../components/BrandBar';
import DeviceList from '../components/DeviceList';
import { observer } from 'mobx-react-lite';
import { Context } from '../index';
import { fetchBrands, fetchDevices, fetchTypes } from '../http/deviceApi';
import Pages from '../components/Pages';

const Shop = observer(() => {

    const {device} = useContext(Context)

    useEffect(()=>{
        fetchTypes().then(data=>device.setTypes(data))
        fetchBrands().then(data=>device.setBrand(data))
        fetchDevices().then(data=>{device.setDevice(data.rows)})
    },[])

    return (
        <div style={{paddingTop:'70px', background:'black'}} className='d-flex'>
        <div style={{marginTop:'30px', background:'white', borderRadius:'40px 40px 0px 0px'}} className='d-flex justify-content-center pt-5 pb-5'>
            <div style={{height:'350px'}} className=' d-flex justify-conent-center row gy-5'>
                <TypeBar/>
                <BrandBar/>
            </div>
            <div style={{width: '60%'}}>
                <DeviceList/>
            </div>
            <Pages/>
        </div>
        </div>
    );
});

export default Shop;
