import { observer } from 'mobx-react-lite';
import React, { useContext, useState } from 'react';
import { Context } from '../index';
import './TypeBar.css';

const TypeBar = observer(() => {
    const [carTypes, setCarTypes] = useState(false)
    const {device} = useContext(Context)

    return (
        <div className='d-flex'>
            <div className="dropdown ">
                {device.types.map(type => (<div className='' key={type.id}>
                    <div type='button' id='drop-button' style={{width:'157px'}} onClick={()=> {device.setSelectedType(type);}} className={type.id === device.selectedType.id ? 'd-flex justify-content-center text-decoration-none p-2 border border-top-0 fs-7 bg-primary text-light' : 'd-flex justify-content-center text-decoration-none p-2 border border-top-0 fs-7 text-dark'}>
                        {type.name}
                    </div>
                </div>)
            ) } 
            </div>
        </div>
    );
});

export default TypeBar;
