import { observer } from 'mobx-react-lite';
import React, { useContext, useState } from 'react';
import { Context } from '../index';
import './TypeBar.css';

const TypeBar = observer(() => {
    const [carTypes, setCarTypes] = useState(false)
    const {device} = useContext(Context)
    const [dropList, setDropList] = useState('d-flex justify-content-center text-decoration-none p-2 border border-top-0 fs-7 text-dark')
    const [dropListBool, setDropListBool] = useState(false)

    function fs(a){
        if(dropListBool === device.selectedType){
            setDropList('d-flex justify-content-center text-decoration-none p-2 border border-top-0 fs-7 bg-primary text-white')
        }else{setDropList('d-flex justify-content-center text-decoration-none p-2 border border-top-0 fs-7 text-dark')}
    }

    return (
        <div className='d-flex'>
            <div class="dropdown">
                <button onClick={()=>setCarTypes(!carTypes)} class="btn btn-secondary dropdown-toggle" type="button" >
                    Dropdown button
                </button>
                {carTypes ? <div className ="dropup-menu px-1">
                {device.types.map(type => (<div>
                    <div type='button' id='drop-button' style={{width:'157px'}} key={type.id} onClick={()=> {device.setSelectedType(type); fs()}} className={dropList}>
                        {type.name}
                    </div>
                </div>)
            ) } </div>
                : 
                <ul className = "dropdown-menu"></ul>
                }
            </div>
        </div>
    );
});

export default TypeBar;
